import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { mockTimeSlots, mockAppointments, mockDoctors, mockSpecializations } from '../../mockData';
import '../styles/BookingForm.css';

/**
 * BookingForm Component - Book Medical Appointment
 * 
 * MEDIBOOK - Refactored for Medical Appointments
 * 
 * Learning Goals:
 * 1. Multi-step form state management
 * 2. Date and time selection
 * 3. Form validation and error handling
 * 4. Conditional rendering based on form steps
 * 5. Pre-filled form with doctor information
 * 
 * React Concepts:
 * - useLocation hook to receive data from Dashboard
 * - useState for multi-step form tracking
 * - Form validation before submission
 * - Medical reason for visit tracking
 */

function BookingForm() {
  const location = useLocation();
  const navigate = useNavigate();
  
  // Get selected doctor from Dashboard
  const selectedDoctor = location.state?.selectedDoctor;

  // State for form steps (1: Date/Time, 2: Medical Reason, 3: Confirm)
  const [currentStep, setCurrentStep] = useState(1);

  // State for booking details
  const [bookingData, setBookingData] = useState({
    doctorId: selectedDoctor?.id || null,
    date: '',
    time: '',
    medicalReason: '',
    notes: ''
  });

  // State for validation errors
  const [errors, setErrors] = useState({});

  // If no doctor selected, show message
  if (!selectedDoctor) {
    return (
      <div className="booking-container">
        <div className="no-selection">
          <h2>‚ùå No Doctor Selected</h2>
          <p>Please select a doctor from the specialization list before booking.</p>
          <button className="back-btn" onClick={() => navigate('/dashboard')}>
            ‚Üê Go Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  /**
   * Handle date selection with validation
   * 
   * Validation Rules:
   * - Date must be today or in future
   * - Can't book too far ahead (optional: add 90 days limit)
   */
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    
    // Validation: date must be today or in future
    const today = new Date().toISOString().split('T')[0];
    if (selectedDate < today) {
      setErrors({ ...errors, date: 'Cannot book appointment in the past' });
      return;
    }

    setBookingData({ ...bookingData, date: selectedDate });
    setErrors({ ...errors, date: '' });
  };

  /**
   * Handle time slot selection
   */
  const handleTimeSelect = (time) => {
    setBookingData({ ...bookingData, time });
    setErrors({ ...errors, time: '' });
  };

  /**
   * Handle medical reason input
   */
  const handleMedicalReasonChange = (e) => {
    const reason = e.target.value;
    setBookingData({ ...bookingData, medicalReason: reason });
    
    if (reason.trim().length < 10) {
      setErrors({ ...errors, medicalReason: 'Please provide at least 10 characters' });
    } else {
      setErrors({ ...errors, medicalReason: '' });
    }
  };

  /**
   * Validate current step before moving to next
   */
  const validateStep = () => {
    const newErrors = {};

    if (currentStep === 1) {
      if (!bookingData.date) {
        newErrors.date = 'Please select a date';
      }
      if (!bookingData.time) {
        newErrors.time = 'Please select a time slot';
      }
    }

    if (currentStep === 2) {
      if (bookingData.medicalReason.trim().length < 10) {
        newErrors.medicalReason = 'Please provide a medical reason (at least 10 characters)';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  /**
   * Move to next step
   */
  const handleNextStep = () => {
    if (validateStep()) {
      setCurrentStep(currentStep + 1);
    }
  };

  /**
   * Submit booking - Create medical appointment
   * Medical appointment includes: doctor, specialization, date, time, medical reason
   */
  const handleSubmitBooking = () => {
    if (!validateStep()) return;

    // Get specialization for this doctor
    const specialization = mockSpecializations.find(
      s => s.id === selectedDoctor.specializationId
    );

    // Create medical appointment
    const newAppointment = {
      id: Math.max(...mockAppointments.map(a => a.id), 0) + 1,
      doctorName: selectedDoctor.name,
      specialization: specialization?.name || 'Unknown Specialization',
      clinic: selectedDoctor.clinic,
      date: bookingData.date,
      time: bookingData.time,
      consultationFee: selectedDoctor.consultationFee,
      reason: bookingData.medicalReason,
      status: 'Confirmed',
      bookedAt: new Date().toISOString().split('T')[0]
    };

    // Log appointment for verification (in Phase 2 this will be sent to API)
    console.log('Medical Appointment Booked:', newAppointment);
    
    alert('‚úÖ Medical appointment booked successfully!');
    navigate('/my-appointments');
  };

  return (
    <div className="booking-container">
      <h1>üìÖ Book Doctor Appointment with {selectedDoctor.name}</h1>

      {/* Service Summary */}
      <div className="service-summary">
        <h3>{selectedService.name}</h3>
        <p>{selectedService.type}</p>
        <p className="price">‚Çπ{selectedService.price} ‚Ä¢ {selectedService.duration} mins</p>
      </div>

      {/* Progress Indicator */}
      <div className="progress-bar">
        <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>1. Select Date</div>
        <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>2. Select Time</div>
        <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>3. Confirm</div>
      </div>

      {/* Step 1: Date Selection */}
      {currentStep === 1 && (
        <div className="form-step">
          <h2>Select Date</h2>
          <input
            type="date"
            value={bookingData.date}
            onChange={handleDateChange}
            min={new Date().toISOString().split('T')[0]}
            className="date-input"
          />
          {errors.date && <p className="error-text">{errors.date}</p>}
        </div>
      )}

      {/* Step 2: Time Selection */}
      {currentStep === 2 && (
        <div className="form-step">
          <h2>Select Time Slot for {bookingData.date}</h2>
          <div className="time-slots">
            {mockTimeSlots.map((slot) => (
              <button
                key={slot.id}
                className={`time-slot ${
                  slot.available ? 'available' : 'unavailable'
                } ${bookingData.time === slot.time ? 'selected' : ''}`}
                onClick={() => slot.available && handleTimeSelect(slot.time)}
                disabled={!slot.available}
              >
                {slot.time}
                {!slot.available && ' (Booked)'}
              </button>
            ))}
          </div>
          {errors.time && <p className="error-text">{errors.time}</p>}
        </div>
      )}

      {/* Step 3: Confirmation */}
      {currentStep === 3 && (
        <div className="form-step">
          <h2>Confirm Your Booking</h2>
          <div className="confirmation-details">
            <div className="detail-row">
              <span>Service:</span>
              <span>{selectedService.name}</span>
            </div>
            <div className="detail-row">
              <span>Type:</span>
              <span>{selectedService.type}</span>
            </div>
            <div className="detail-row">
              <span>Date:</span>
              <span>{bookingData.date}</span>
            </div>
            <div className="detail-row">
              <span>Time:</span>
              <span>{bookingData.time}</span>
            </div>
            <div className="detail-row">
              <span>Duration:</span>
              <span>{selectedService.duration} minutes</span>
            </div>
            <div className="detail-row total">
              <span>Total Amount:</span>
              <span>‚Çπ{selectedService.price}</span>
            </div>
          </div>

          {/* Additional Notes */}
          <textarea
            placeholder="Add any special notes or requirements..."
            value={bookingData.notes}
            onChange={(e) => setBookingData({ ...bookingData, notes: e.target.value })}
            className="notes-textarea"
          />
        </div>
      )}

      {/* Navigation Buttons */}
      <div className="button-group">
        {currentStep > 1 && (
          <button 
            className="btn-secondary"
            onClick={() => setCurrentStep(currentStep - 1)}
          >
            ‚Üê Back
          </button>
        )}

        {currentStep < 3 && (
          <button 
            className="btn-primary"
            onClick={handleNextStep}
          >
            Next ‚Üí
          </button>
        )}

        {currentStep === 3 && (
          <button 
            className="btn-success"
            onClick={handleSubmitBooking}
          >
            ‚úì Confirm Booking
          </button>
        )}

        <button 
          className="btn-cancel"
          onClick={() => navigate('/dashboard')}
        >
          Cancel
        </button>
      </div>
    </div>
  );
}

export default BookingForm;
