import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { mockSpecializations, mockUser } from '../../mockData';
import { apiRequest } from '../../api/client';
import '../styles/Dashboard.css';

/**
 * Dashboard Component - Medical Specialization & Doctor Browser
 * 
 * MEDIBOOK - Refactored for Medical-Only Appointments
 * 
 * Learning Goals:
 * 1. Two-level hierarchy: Specializations ‚Üí Doctors
 * 2. Array filtering for dependent data
 * 3. useMemo for performance optimization
 * 4. Conditional rendering with multiple states
 * 
 * React Concepts:
 * - map() transforms arrays into JSX
 * - filter() finds relevant doctors by specialization
 * - useMemo() prevents unnecessary re-renders
 * - State dependencies: selectedSpecialization ‚Üí filteredDoctors
 */

function Dashboard() {
  const navigate = useNavigate();
  
  // State: Step 1 - Select Specialization
  const [selectedSpecialization, setSelectedSpecialization] = useState(null);
  
  // State: Step 2 - Search within doctors
  const [searchTerm, setSearchTerm] = useState('');

  // Doctors loaded from backend
  const [doctors, setDoctors] = useState([]);
  const [doctorsError, setDoctorsError] = useState('');
  const [appointmentsCount, setAppointmentsCount] = useState(0);

  useEffect(() => {
    const loadDoctors = async () => {
      try {
        const data = await apiRequest('/api/doctors');
        setDoctors(data);
      } catch (err) {
        setDoctorsError(err.message || 'Failed to load doctors');
      }
    };

    const loadAppointmentCount = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const data = await apiRequest('/api/appointments', { token });
        setAppointmentsCount(data.length);
      } catch (err) {
        setAppointmentsCount(0);
      }
    };

    loadDoctors();
    loadAppointmentCount();
  }, []);

  /**
   * React Concept: useMemo - Performance Optimization
   * 
   * WHY: filteredDoctors depends on selectedSpecialization and searchTerm
   * When these change, recalculate. Otherwise, use cached result.
   * This prevents unnecessary rendering of the entire doctor list.
   */
  const filteredDoctors = useMemo(() => {
    if (!selectedSpecialization) return [];
    
    return doctors
      .filter(doctor => doctor.specializationId === selectedSpecialization.id)
      .filter(doctor => 
        doctor.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        doctor.qualifications.toLowerCase().includes(searchTerm.toLowerCase())
      );
  }, [selectedSpecialization, searchTerm, doctors]);

  /**
   * Handle booking navigation
   * Pre-fill booking form with selected doctor
   */
  const handleBookDoctor = (doctor) => {
    navigate('/booking', { state: { selectedDoctor: doctor } });
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <h1>üëã Welcome, {mockUser.name}!</h1>
        <p>Book your medical appointment with top doctors</p>
      </div>

      {/* Search Bar */}
      <div className="search-section">
        <input
          type="text"
          placeholder="Search for doctors, services..."
          className="search-input"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <button className="action-btn" onClick={() => navigate('/my-appointments')}>
          üìÖ My Appointments ({appointmentsCount})
        </button>
        <button className="action-btn" onClick={() => navigate('/profile')}>
          üë§ My Profile
        </button>
      </div>

      {/* Step 1: Specialization Selection */}
      <div className="specializations-section">
        <h2>üè• Medical Specializations</h2>
        
        <div className="specializations-grid">
          {mockSpecializations.map((spec) => (
            <div
              key={spec.id}
              className={`specialization-card ${
                selectedSpecialization?.id === spec.id ? 'selected' : ''
              }`}
              onClick={() => {
                setSelectedSpecialization(spec);
                setSearchTerm(''); // Clear search when changing specialization
              }}
            >
              <div className="spec-icon">{spec.icon}</div>
              <h3>{spec.name}</h3>
              <p className="spec-description">{spec.description}</p>
              <small className="doctor-count">
                {doctors.filter(d => d.specializationId === spec.id).length} Doctors
              </small>
            </div>
          ))}
        </div>
      </div>

      {/* Step 2: Doctor Selection (filtered by specialization) */}
      {selectedSpecialization && (
        <div className="doctors-section">
          <div className="section-header">
            <h2>üë®‚Äç‚öïÔ∏è Select a Doctor - {selectedSpecialization.name}</h2>
            <button 
              className="clear-btn"
              onClick={() => setSelectedSpecialization(null)}
            >
              ‚Üê Change Specialization
            </button>
          </div>

          {/* Search within selected specialization */}
          <div className="search-section">
            <input
              type="text"
              placeholder="Search by doctor name or qualifications..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          {/* Display filtered doctors */}
          {doctorsError && <p className="no-results">‚ö†Ô∏è {doctorsError}</p>}
          {!doctorsError && filteredDoctors.length === 0 ? (
            <p className="no-results">
              {searchTerm 
                ? "No doctors found matching your search." 
                : "No doctors available in this specialization."}
            </p>
          ) : (
            <div className="doctors-grid">
              {filteredDoctors.map((doctor) => (
                <div key={doctor.id} className="doctor-card">
                  {/* Doctor Image/Icon */}
                  <div className="doctor-icon">{doctor.image}</div>
                  
                  {/* Doctor Info */}
                  <h3>{doctor.name}</h3>
                  <p className="qualifications">{doctor.qualifications}</p>
                  <p className="experience">‚è≥ {doctor.experience}</p>
                  <p className="clinic">üè• {doctor.clinic}</p>
                  
                  {/* Consultation Details */}
                  <div className="consultation-info">
                    <span className="fee">‚Çπ{doctor.consultationFee}</span>
                    <span className="duration">{doctor.duration} mins</span>
                  </div>
                  
                  {/* Book Button */}
                  <button 
                    className="book-btn"
                    onClick={() => handleBookDoctor(doctor)}
                  >
                    Book Appointment ‚Üí
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Info Section */}
      <div className="info-section">
        <h3>‚ÑπÔ∏è How Medibook Works</h3>
        <ol>
          <li><strong>Select Specialization:</strong> Choose your medical need (Dermatology, Cardiology, etc.)</li>
          <li><strong>Pick a Doctor:</strong> View doctors in that specialty with credentials</li>
          <li><strong>Choose Date & Time:</strong> Pick convenient appointment slots</li>
          <li><strong>Provide Reason:</strong> Tell us your medical concern</li>
          <li><strong>Confirm:</strong> Complete your booking and get confirmation</li>
        </ol>
      </div>
    </div>
  );
}

export default Dashboard;
