import React from 'react';
import { useNavigate } from 'react-router-dom';
import { mockAppointments } from '../../mockData';
import '../styles/MyAppointments.css';

/**
 * MyAppointments Component - Display all medical appointments
 * 
 * MEDIBOOK - Refactored for Medical Appointments
 * 
 * Learning Goals:
 * 1. Display medical appointments with doctor and specialization info
 * 2. Conditional rendering based on appointment status/date
 * 3. Medical appointment-specific actions
 * 4. Empty state handling
 */

function MyAppointments() {
  const navigate = useNavigate();

  /**
   * Handle appointment cancellation
   */
  const handleCancel = (appointmentId) => {
    if (window.confirm('Are you sure you want to cancel this medical appointment?')) {
      alert('‚úÖ Medical appointment cancelled successfully');
      // In Phase 2: API call to delete appointment
    }
  };

  /**
   * Handle appointment rescheduling
   * Navigate back to dashboard to select new doctor/date/time
   */
  const handleReschedule = (appointment) => {
    navigate('/dashboard', { 
      state: { rescheduleId: appointment.id }
    });
  };

  // Status badge styling
  const getStatusClass = (status) => {
    switch (status) {
      case 'Confirmed':
        return 'status-confirmed';
      case 'Cancelled':
        return 'status-cancelled';
      case 'Completed':
        return 'status-completed';
      default:
        return 'status-pending';
    }
  };

  return (
    <div className="appointments-container">
      <h1>üìÖ My Medical Appointments</h1>

      {/* Empty State */}
      {mockAppointments.length === 0 ? (
        <div className="empty-state">
          <p>üì≠ No appointments booked yet</p>
          <button 
            className="book-btn"
            onClick={() => navigate('/dashboard')}
          >
            Book Your First Doctor Appointment
          </button>
        </div>
      ) : (
        <div className="appointments-list">
          {/* Upcoming Medical Appointments */}
          <h2>Upcoming Appointments</h2>
          <div className="appointments-grid">
            {mockAppointments
              .filter(apt => new Date(apt.date) >= new Date() && apt.status !== 'Cancelled')
              .map((appointment) => (
                <div key={appointment.id} className="appointment-card medical">
                  {/* Appointment Header */}
                  <div className="appointment-header">
                    <h3>{appointment.doctorName}</h3>
                    <span className={`status-badge ${getStatusClass(appointment.status)}`}>
                      {appointment.status}
                    </span>
                  </div>

                  {/* Medical Information */}
                  <div className="appointment-details">
                    <p><strong>üë®‚Äç‚öïÔ∏è Doctor:</strong> {appointment.doctorName}</p>
                    <p><strong>üè• Specialization:</strong> {appointment.specialization}</p>
                    <p><strong>üè¢ Clinic:</strong> {appointment.clinic}</p>
                    <p><strong>üìÖ Date:</strong> {new Date(appointment.date).toLocaleDateString()}</p>
                    <p><strong>‚è∞ Time:</strong> {appointment.time}</p>
                    <p><strong>üí∞ Consultation Fee:</strong> ‚Çπ{appointment.consultationFee}</p>
                    <div className="reason-box">
                      <p><strong>üîç Reason for Visit:</strong></p>
                      <p className="reason-text">{appointment.reason}</p>
                    </div>
                    <p><strong>üìù Booked on:</strong> {appointment.bookedAt}</p>
                  </div>

                  {/* Action Buttons */}
                  <div className="appointment-actions">
                    <button 
                      className="btn-reschedule"
                      onClick={() => handleReschedule(appointment)}
                    >
                      üìÖ Reschedule
                    </button>
                    <button 
                      className="btn-cancel"
                      onClick={() => handleCancel(appointment.id)}
                    >
                      ‚úï Cancel
                    </button>
                  </div>
                </div>
              ))}
          </div>

          {/* Past Appointments */}
          {mockAppointments.some(apt => new Date(apt.date) < new Date()) && (
            <>
              <h2>Completed Appointments</h2>
              <div className="appointments-grid">
                {mockAppointments
                  .filter(apt => new Date(apt.date) < new Date())
                  .map((appointment) => (
                    <div key={appointment.id} className="appointment-card medical past">
                      <div className="appointment-header">
                        <h3>{appointment.doctorName}</h3>
                        <span className="status-badge status-completed">Completed</span>
                      </div>

                      <div className="appointment-details">
                        <p><strong>üë®‚Äç‚öïÔ∏è Doctor:</strong> {appointment.doctorName}</p>
                        <p><strong>üè• Specialization:</strong> {appointment.specialization}</p>
                        <p><strong>üè¢ Clinic:</strong> {appointment.clinic}</p>
                        <p><strong>üìÖ Date:</strong> {new Date(appointment.date).toLocaleDateString()}</p>
                        <p><strong>‚è∞ Time:</strong> {appointment.time}</p>
                        <p><strong>üí∞ Consultation Fee:</strong> ‚Çπ{appointment.consultationFee}</p>
                        <div className="reason-box">
                          <p><strong>üîç Reason for Visit:</strong></p>
                          <p className="reason-text">{appointment.reason}</p>
                        </div>
                      </div>

                      <div className="appointment-actions">
                        <button className="btn-rebook">
                          üîÑ Book Again
                        </button>
                        <button className="btn-download">
                          üì• Download Receipt
                        </button>
                      </div>
                    </div>
                  ))}
              </div>
            </>
          )}
        </div>
      )}

      {/* Back Button */}
      <button 
        className="back-btn"
        onClick={() => navigate('/dashboard')}
      >
        ‚Üê Back to Dashboard
      </button>
    </div>
  );
}

export default MyAppointments;
